import { GoogleGenerativeAI } from '@google/generative-ai';
import { Candidate } from '../types';

const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY || '');

interface ExtractedCVData {
  name: string;
  email: string;
  phone?: string;
  location?: string;
  workExperience: Array<{
    company: string;
    position: string;
    startDate: string;
    endDate?: string;
    description: string;
  }>;
  education: Array<{
    institution: string;
    degree: string;
    field: string;
    graduationDate: string;
  }>;
  skills: string[];
  languages: string[];
  certifications: string[];
}

export const processCV = async (file: File): Promise<ExtractedCVData> => {
  try {
    // Convert file to base64
    const base64Data = await fileToBase64(file);

    // Use Gemini to analyze the CV content
    const model = genAI.getGenerativeModel({ model: "gemini-pro-vision" });

    const prompt = `Analyze this CV and extract the following information in a structured format:
    - Full name
    - Email address
    - Phone number (if available)
    - Location (if available)
    - Work experience (company, position, dates, description)
    - Education (institution, degree, field, graduation date)
    - Skills
    - Languages
    - Certifications

    Format the response as a JSON object with these exact keys:
    {
      "name": string,
      "email": string,
      "phone": string | null,
      "location": string | null,
      "workExperience": Array<{
        "company": string,
        "position": string,
        "startDate": string,
        "endDate": string | null,
        "description": string
      }>,
      "education": Array<{
        "institution": string,
        "degree": string,
        "field": string,
        "graduationDate": string
      }>,
      "skills": string[],
      "languages": string[],
      "certifications": string[]
    }`;

    const result = await model.generateContent([
      prompt,
      {
        inlineData: {
          mimeType: file.type,
          data: base64Data
        }
      }
    ]);

    const response = await result.response;
    const text = response.text();

    // Parse the JSON response
    const extractedData: ExtractedCVData = JSON.parse(text);

    return extractedData;
  } catch (error) {
    console.error('Error processing CV:', error);
    throw new Error('Failed to process CV');
  }
};

// Helper function to convert File to base64
const fileToBase64 = (file: File): Promise<string> => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => {
      const base64String = reader.result as string;
      // Remove the data URL prefix (e.g., "data:image/jpeg;base64,")
      const base64Data = base64String.split(',')[1];
      resolve(base64Data);
    };
    reader.onerror = error => reject(error);
  });
};

// Convert extracted data to Candidate type
export const convertToCandidate = (extractedData: ExtractedCVData): Omit<Candidate, 'id'> => {
  return {
    name: extractedData.name,
    email: extractedData.email,
    phone: extractedData.phone || '',
    location: extractedData.location || '',
    status: 'new',
    experience: extractedData.workExperience.map(exp => ({
      company: exp.company,
      position: exp.position,
      startDate: exp.startDate,
      endDate: exp.endDate || 'Present',
      description: exp.description
    })),
    education: extractedData.education.map(edu => ({
      institution: edu.institution,
      degree: edu.degree,
      field: edu.field,
      graduationDate: edu.graduationDate
    })),
    skills: extractedData.skills,
    languages: extractedData.languages,
    certifications: extractedData.certifications,
    notes: '',
    createdAt: new Date().toISOString(),
    updatedAt: new Date().toISOString()
  };
};
