import React, { useState } from 'react';
import { PlusIcon, MagnifyingGlassIcon, FunnelIcon } from '@heroicons/react/24/outline';
import { JobPosting } from '../types';
import JobPostingForm from '../components/JobPostingForm';

const JobPostings: React.FC = () => {
  const [jobPostings, setJobPostings] = useState<JobPosting[]>([
    {
      id: '1',
      title: 'Store Manager',
      department: 'Management',
      location: 'New York',
      status: 'published',
      employmentType: 'full-time',
      shift: 'day',
      salary: {
        min: 50000,
        max: 70000,
        currency: 'USD'
      },
      experience: 'manager',
      storeType: 'hypermarket',
      physicalRequirements: ['Ability to lift 50lbs', 'Stand for long periods'],
      benefits: ['Health Insurance', '401k', 'Paid Time Off']
    },
    {
      id: '2',
      title: 'Cashier',
      department: 'Cashier',
      location: 'New York',
      status: 'draft',
      employmentType: 'part-time',
      shift: 'flexible',
      salary: {
        min: 15,
        max: 20,
        currency: 'USD'
      },
      experience: 'entry',
      storeType: 'hypermarket',
      physicalRequirements: ['Stand for long periods'],
      benefits: ['Employee Discount']
    },
    {
      id: '3',
      title: 'Stock Manager',
      department: 'Stock',
      location: 'New York',
      status: 'published',
      employmentType: 'full-time',
      shift: 'night',
      salary: {
        min: 40000,
        max: 55000,
        currency: 'USD'
      },
      experience: 'intermediate',
      storeType: 'hypermarket',
      physicalRequirements: ['Ability to lift 50lbs', 'Stand for long periods'],
      benefits: ['Health Insurance', '401k', 'Paid Time Off']
    }
  ]);

  const [searchTerm, setSearchTerm] = useState('');
  const [departmentFilter, setDepartmentFilter] = useState<string>('all');
  const [statusFilter, setStatusFilter] = useState<string>('all');
  const [isFormOpen, setIsFormOpen] = useState(false);
  const [editingPosting, setEditingPosting] = useState<JobPosting | null>(null);

  const departments = ['all', ...new Set(jobPostings.map(posting => posting.department))];
  const statuses = ['all', 'draft', 'published', 'closed'];

  const filteredPostings = jobPostings.filter(posting => {
    const matchesSearch = posting.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         posting.department.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         posting.location.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = departmentFilter === 'all' || posting.department === departmentFilter;
    const matchesStatus = statusFilter === 'all' || posting.status === statusFilter;
    return matchesSearch && matchesDepartment && matchesStatus;
  });

  const handleCreatePosting = (newPosting: Omit<JobPosting, 'id'>) => {
    const posting: JobPosting = {
      ...newPosting,
      id: (jobPostings.length + 1).toString(),
    };
    setJobPostings(prev => [...prev, posting]);
    setIsFormOpen(false);
    setEditingPosting(null);
  };

  const handleEditPosting = (posting: JobPosting) => {
    setEditingPosting(posting);
    setIsFormOpen(true);
  };

  const handleUpdatePosting = (updatedPosting: Omit<JobPosting, 'id'>) => {
    if (!editingPosting) return;

    setJobPostings(prev => prev.map(posting => 
      posting.id === editingPosting.id 
        ? { ...updatedPosting, id: posting.id }
        : posting
    ));
    setIsFormOpen(false);
    setEditingPosting(null);
  };

  return (
    <div>
      <div className="sm:flex sm:items-center">
        <div className="sm:flex-auto">
          <h1 className="text-2xl font-semibold text-gray-900">Job Postings</h1>
          <p className="mt-2 text-sm text-gray-700">
            A list of all job postings in your organization.
          </p>
        </div>
        <div className="mt-4 sm:mt-0 sm:ml-16 sm:flex-none">
          <button
            type="button"
            onClick={() => setIsFormOpen(true)}
            className="inline-flex items-center justify-center rounded-md border border-transparent bg-indigo-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 sm:w-auto"
          >
            <PlusIcon className="-ml-1 mr-2 h-5 w-5" aria-hidden="true" />
            New Job Posting
          </button>
        </div>
      </div>

      {/* Search and Filters */}
      <div className="mt-4 flex flex-col sm:flex-row gap-4">
        <div className="flex-1">
          <div className="relative rounded-md shadow-sm">
            <div className="pointer-events-none absolute inset-y-0 left-0 flex items-center pl-3">
              <MagnifyingGlassIcon className="h-5 w-5 text-gray-400" aria-hidden="true" />
            </div>
            <input
              type="text"
              className="block w-full rounded-md border-0 py-1.5 pl-10 text-gray-900 ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              placeholder="Search by title, department, or location..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
        </div>
        <div className="flex gap-4">
          <div className="flex items-center">
            <FunnelIcon className="h-5 w-5 text-gray-400 mr-2" aria-hidden="true" />
            <select
              className="rounded-md border-0 py-1.5 pl-3 pr-10 text-gray-900 ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-indigo-600 sm:text-sm sm:leading-6"
              value={departmentFilter}
              onChange={(e) => setDepartmentFilter(e.target.value)}
            >
              {departments.map((dept) => (
                <option key={dept} value={dept}>
                  {dept.charAt(0).toUpperCase() + dept.slice(1)}
                </option>
              ))}
            </select>
          </div>
          <div className="flex items-center">
            <select
              className="rounded-md border-0 py-1.5 pl-3 pr-10 text-gray-900 ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-indigo-600 sm:text-sm sm:leading-6"
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
            >
              {statuses.map((status) => (
                <option key={status} value={status}>
                  {status.charAt(0).toUpperCase() + status.slice(1)}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      <div className="mt-8 flex flex-col">
        <div className="-my-2 -mx-4 overflow-x-auto sm:-mx-6 lg:-mx-8">
          <div className="inline-block min-w-full py-2 align-middle md:px-6 lg:px-8">
            <div className="overflow-hidden shadow ring-1 ring-black ring-opacity-5 md:rounded-lg">
              <table className="min-w-full divide-y divide-gray-300">
                <thead className="bg-gray-50">
                  <tr>
                    <th scope="col" className="py-3.5 pl-4 pr-3 text-left text-sm font-semibold text-gray-900 sm:pl-6">
                      Title
                    </th>
                    <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">
                      Department
                    </th>
                    <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">
                      Location
                    </th>
                    <th scope="col" className="px-3 py-3.5 text-left text-sm font-semibold text-gray-900">
                      Status
                    </th>
                    <th scope="col" className="relative py-3.5 pl-3 pr-4 sm:pr-6">
                      <span className="sr-only">Actions</span>
                    </th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200 bg-white">
                  {filteredPostings.map((posting) => (
                    <tr key={posting.id}>
                      <td className="whitespace-nowrap py-4 pl-4 pr-3 text-sm font-medium text-gray-900 sm:pl-6">
                        {posting.title}
                      </td>
                      <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{posting.department}</td>
                      <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">{posting.location}</td>
                      <td className="whitespace-nowrap px-3 py-4 text-sm text-gray-500">
                        <span className={`inline-flex rounded-full px-2 text-xs font-semibold leading-5 ${
                          posting.status === 'published' ? 'bg-green-100 text-green-800' :
                          posting.status === 'draft' ? 'bg-yellow-100 text-yellow-800' :
                          'bg-red-100 text-red-800'
                        }`}>
                          {posting.status}
                        </span>
                      </td>
                      <td className="relative whitespace-nowrap py-4 pl-3 pr-4 text-right text-sm font-medium sm:pr-6">
                        <button 
                          onClick={() => handleEditPosting(posting)}
                          className="text-indigo-600 hover:text-indigo-900"
                        >
                          Edit
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>

      {/* Modal */}
      {isFormOpen && (
        <div className="fixed inset-0 bg-gray-500 bg-opacity-75 overflow-y-auto h-full w-full z-50">
          <div className="relative top-4 sm:top-8 md:top-20 mx-auto p-4 sm:p-6 border w-[95%] sm:w-[90%] md:w-[800px] shadow-lg rounded-md bg-white">
            <div className="flex justify-between items-center mb-4">
              <h3 className="text-lg font-medium text-gray-900">
                {editingPosting ? 'Edit Job Posting' : 'Create New Job Posting'}
              </h3>
              <button
                onClick={() => {
                  setIsFormOpen(false);
                  setEditingPosting(null);
                }}
                className="text-gray-400 hover:text-gray-500"
              >
                <span className="sr-only">Close</span>
                <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>
            <JobPostingForm 
              onSubmit={editingPosting ? handleUpdatePosting : handleCreatePosting} 
              onCancel={() => {
                setIsFormOpen(false);
                setEditingPosting(null);
              }}
              initialData={editingPosting || undefined}
              mode={editingPosting ? 'edit' : 'create'}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default JobPostings;
