import React, { useState } from 'react';
import { JobPosting } from '../types';

interface JobPostingFormProps {
  onSubmit: (posting: Omit<JobPosting, 'id'>) => void;
  onCancel: () => void;
}

const JobPostingForm: React.FC<JobPostingFormProps> = ({ onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: '',
    department: '',
    location: '',
    status: 'draft' as const,
    employmentType: 'full-time' as const,
    shift: 'day' as const,
    salary: {
      min: '',
      max: '',
      currency: 'USD'
    },
    experience: 'entry' as const,
    storeType: 'hypermarket' as const,
    physicalRequirements: [] as string[],
    benefits: [] as string[]
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const submissionData: Omit<JobPosting, 'id'> = {
      ...formData,
      salary: {
        min: Number(formData.salary.min),
        max: Number(formData.salary.max),
        currency: formData.salary.currency
      }
    };
    onSubmit(submissionData);
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    if (name.startsWith('salary.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        salary: {
