import React, { useState } from 'react';
import { JobPosting } from '../types';

interface JobPostingFormProps {
  onSubmit: (posting: Omit<JobPosting, 'id'>) => void;
  onCancel: () => void;
}

interface FormData {
  title: string;
  department: string;
  location: string;
  description: string;
  requirements: string;
  status: 'draft' | 'published' | 'closed';
  employmentType: 'full-time' | 'part-time' | 'temporary';
  shift: 'day' | 'night' | 'weekend' | 'flexible';
  salary: {
    min: string;
    max: string;
    currency: string;
  };
  experience: 'entry' | 'intermediate' | 'senior' | 'manager';
  storeType: 'hypermarket' | 'supermarket' | 'convenience';
  physicalRequirements: string[];
  benefits: string[];
}

const JobPostingForm: React.FC<JobPostingFormProps> = ({ onSubmit, onCancel }) => {
  const [formData, setFormData] = useState<FormData>({
    title: '',
    department: '',
    location: '',
    description: '',
    requirements: '',
    status: 'draft',
    employmentType: 'full-time',
    shift: 'day',
    salary: {
      min: '',
      max: '',
      currency: 'USD'
    },
    experience: 'entry',
    storeType: 'hypermarket',
    physicalRequirements: [],
    benefits: []
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const submissionData: Omit<JobPosting, 'id'> = {
      ...formData,
      salary: {
        min: Number(formData.salary.min),
        max: Number(formData.salary.max),
        currency: formData.salary.currency
      }
    };
    onSubmit(submissionData);
  };

  const handleChange = (
    e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value } = e.target;
    if (name.startsWith('salary.')) {
      const field = name.split('.')[1];
      setFormData(prev => ({
        ...prev,
        salary: { ...prev.salary, [field]: value }
      }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleArrayChange = (e: React.ChangeEvent<HTMLTextAreaElement>, field: 'physicalRequirements' | 'benefits') => {
    const value = e.target.value;
    setFormData(prev => ({
      ...prev,
      [field]: value.split('\n').filter(item => item.trim() !== '')
    }));
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div>
        <label htmlFor="title" className="block text-sm font-medium text-gray-700">
          Job Title
        </label>
        <input
          type="text"
          name="title"
          id="title"
          required
          value={formData.title}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
        />
      </div>

      <div>
        <label htmlFor="department" className="block text-sm font-medium text-gray-700">
          Department
        </label>
        <select
          name="department"
          id="department"
          required
          value={formData.department}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
        >
          <option value="">Select a department</option>
          <option value="Cashier">Cashier</option>
          <option value="Stock">Stock Management</option>
          <option value="Produce">Produce</option>
          <option value="Meat">Meat & Seafood</option>
          <option value="Bakery">Bakery</option>
          <option value="Deli">Deli</option>
          <option value="Customer Service">Customer Service</option>
          <option value="Security">Security</option>
          <option value="Maintenance">Maintenance</option>
          <option value="Management">Store Management</option>
        </select>
      </div>

      <div>
        <label htmlFor="storeType" className="block text-sm font-medium text-gray-700">
          Store Type
        </label>
        <select
          name="storeType"
          id="storeType"
          required
          value={formData.storeType}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
        >
          <option value="hypermarket">Hypermarket</option>
          <option value="supermarket">Supermarket</option>
          <option value="convenience">Convenience Store</option>
        </select>
      </div>

      <div>
        <label htmlFor="employmentType" className="block text-sm font-medium text-gray-700">
          Employment Type
        </label>
        <select
          name="employmentType"
          id="employmentType"
          required
          value={formData.employmentType}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
        >
          <option value="full-time">Full Time</option>
          <option value="part-time">Part Time</option>
          <option value="temporary">Temporary</option>
        </select>
      </div>

      <div>
        <label htmlFor="shift" className="block text-sm font-medium text-gray-700">
          Shift
        </label>
        <select
          name="shift"
          id="shift"
          required
          value={formData.shift}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
        >
          <option value="day">Day Shift</option>
          <option value="night">Night Shift</option>
          <option value="weekend">Weekend</option>
          <option value="flexible">Flexible</option>
        </select>
      </div>

      <div className="grid grid-cols-3 gap-4">
        <div>
          <label htmlFor="salary.min" className="block text-sm font-medium text-gray-700">
            Min Salary
          </label>
          <input
            type="number"
            name="salary.min"
            id="salary.min"
            required
            value={formData.salary.min}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          />
        </div>
        <div>
          <label htmlFor="salary.max" className="block text-sm font-medium text-gray-700">
            Max Salary
          </label>
          <input
            type="number"
            name="salary.max"
            id="salary.max"
            required
            value={formData.salary.max}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          />
        </div>
        <div>
          <label htmlFor="salary.currency" className="block text-sm font-medium text-gray-700">
            Currency
          </label>
          <select
            name="salary.currency"
            id="salary.currency"
            required
            value={formData.salary.currency}
            onChange={handleChange}
            className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          >
            <option value="USD">USD</option>
            <option value="EUR">EUR</option>
            <option value="GBP">GBP</option>
          </select>
        </div>
      </div>

      <div>
        <label htmlFor="experience" className="block text-sm font-medium text-gray-700">
          Experience Level
        </label>
        <select
          name="experience"
          id="experience"
          required
          value={formData.experience}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
        >
          <option value="entry">Entry Level</option>
          <option value="intermediate">Intermediate</option>
          <option value="senior">Senior</option>
          <option value="manager">Manager</option>
        </select>
      </div>

      <div>
        <label htmlFor="description" className="block text-sm font-medium text-gray-700">
          Job Description
        </label>
        <textarea
          name="description"
          id="description"
          rows={4}
          required
          value={formData.description}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          placeholder="Describe the role, responsibilities, and daily tasks..."
        />
      </div>

      <div>
        <label htmlFor="requirements" className="block text-sm font-medium text-gray-700">
          Requirements
        </label>
        <textarea
          name="requirements"
          id="requirements"
          rows={4}
          required
          value={formData.requirements}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          placeholder="List required qualifications, skills, and experience..."
        />
      </div>

      <div>
        <label htmlFor="physicalRequirements" className="block text-sm font-medium text-gray-700">
          Physical Requirements
        </label>
        <textarea
          name="physicalRequirements"
          id="physicalRequirements"
          rows={3}
          required
          value={formData.physicalRequirements.join('\n')}
          onChange={(e) => handleArrayChange(e, 'physicalRequirements')}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          placeholder="List physical requirements (one per line)..."
        />
      </div>

      <div>
        <label htmlFor="benefits" className="block text-sm font-medium text-gray-700">
          Benefits
        </label>
        <textarea
          name="benefits"
          id="benefits"
          rows={3}
          required
          value={formData.benefits.join('\n')}
          onChange={(e) => handleArrayChange(e, 'benefits')}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          placeholder="List benefits (one per line)..."
        />
      </div>

      <div>
        <label htmlFor="status" className="block text-sm font-medium text-gray-700">
          Status
        </label>
        <select
          name="status"
          id="status"
          required
          value={formData.status}
          onChange={handleChange}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
        >
          <option value="draft">Draft</option>
          <option value="published">Published</option>
          <option value="closed">Closed</option>
        </select>
      </div>

      <div className="flex justify-end space-x-3">
        <button
          type="button"
          onClick={onCancel}
          className="rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 shadow-sm hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
        >
          Cancel
        </button>
        <button
          type="submit"
          className="rounded-md border border-transparent bg-indigo-600 px-4 py-2 text-sm font-medium text-white shadow-sm hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2"
        >
          Create Job Posting
        </button>
      </div>
    </form>
  );
};

export default JobPostingForm;
